function search(e){$("#results").html("");if(e!==""){var t=encodeURIComponent(e);var n="http://gdata.youtube.com/feeds/api/videos?q="+t+"&format=5&orderby=relevance&max-results=25&v=2&alt=jsonc";$.ajax({type:"GET",url:n,dataType:"jsonp",success:function(e){if(e.data.items){var t="";$.each(e.data.items,function(e,n){if(n.duration>720&&longS===0){return}if(n.category=="Music"||music==1){var r=encodeURIComponent(n.title).replace(/'/g,"\\'");var i=n.viewCount;var s="http://i.ytimg.com/vi/"+n.id+"/default.jpg";var o=Math.floor(n.duration/60)+":"+(n.duration-Math.floor(n.duration/60)*60);var u="								<div id='result' class='result' onclick=\"submitAndClose('"+n.id+"','"+r+"');\">									<img src='"+s+"' class='thumb'>									<div id='title'>"+n.title+"										<div class='result_info'>"+i+" views â€¢ "+o+"</div>										<input id='add' title='Add several songs' type='button' class='button' value='+' onclick=\"submit('"+n.id+"','"+r+"');\">									</div>								</div>";t+=u}});console.log(t);$("<div style='display:none;'>"+t+"</div>").appendTo("#results").slideDown("slow")}else{$("#video").html("<div id='no'>No Video</div>")}}})}}function submitAndClose(e,t){submit(e,t);$("#results").html("");console.log("sub&closed")}function submit(e,t){console.log($.ajax({type:"GET",url:"php/change.php",async:false,data:"v="+e+"&n="+t+"&pass="+adminpass,success:function(){console.log("added "+e);document.getElementById("search").value="";$("#search").addClass("success")},error:function(){console.log("error in adding");document.getElementById("search").value="";$("#search").addClass("error")}}).responseText);$("#search").focus();setTimeout(function(){$("#search").removeClass("success");$("#search").removeClass("error")},1500);updateList();event.stopPropagation()}var old_input="";var timer=0;$(document).ready(function(){$("#search").focus();$("#base").bind("keyup keypress",function(e){var t=e.keyCode||e.which;if(t==13){e.preventDefault();return false}});$(".search_input").focus();$(".search_input").keyup(function(e){var t=$(this).val();if(t.length<3){$("#results").html("")}if(e.keyCode==13){search(t)}else if(e.keyCode==27){$("#results").html("")}else{timer=100}});setInterval(function(){timer--;if(timer===0){search($(".search_input").val())}},1)})